svg.script:
var b1, b2, b3, b4;
var azap = new AntiZoomAndPan();

function init(e) {
	b1 = new button( 
		20, 20, showB1,
		svgDocument.getElementById("redButtonOn").firstChild.data,
		svgDocument.getElementById("redButtonOff").firstChild.data,
		svgDocument.getElementById("blankButtonHighlight").firstChild.data
	);
	b2 = new button( 
		40, 20, showB2,
		svgDocument.getElementById("greenButtonOn").firstChild.data,
		svgDocument.getElementById("greenButtonOff").firstChild.data,
		svgDocument.getElementById("blankButtonHighlight").firstChild.data
	);
	b2.setLabel(2, -5, "2");
	b3 = new button(
		60, 20, showB3,
		svgDocument.getElementById("blueButtonOn").firstChild.data,
		svgDocument.getElementById("blueButtonOff").firstChild.data,
		svgDocument.getElementById("blankButtonHighlight").firstChild.data
	);
	b4 = new button( 40, 50, showB4 );

	var controls=svgDocument.getElementById("controls");
	b1.realize(controls);
	b2.realize(controls);
	b3.realize(controls);
	b4.realize(controls);

	// call setState functions to set some buttons to false
	// and make all buttons issue callbacks to update labels
	setTimeout( "b1.setState(false)", 0 );
	setTimeout( "b2.setState(true)", 0 );
	setTimeout( "b3.setState(false)", 0 );
	setTimeout( "b4.setState(true)", 0 );

	b1.setHighlight(false);
	b2.setHighlight(false);
	b3.setHighlight(false);

	azap.appendNode(controls);
}

function showB1(val) {
	svgDocument.getElementById("label1").firstChild.data =
		"Button 1 is "+val;
}
function showB2(val) {
	svgDocument.getElementById("label2").firstChild.data =
		"Button 2 is "+val;
}
function showB3(val) {
	svgDocument.getElementById("label3").firstChild.data =
		"Button 3 is "+val;
}
function showB4(val) {
	svgDocument.getElementById("label4").firstChild.data =
		"Button 4 is "+val;
}
