svg.script:
		var multiToolBox;
		var panePopupList
		var azap = new AntiZoomAndPan();

		function init(e) {
			if ( window.svgDocument == null )
				svgDocument = e.ownerDocument;

            var controls  = svgDocument.getElementById("controls");

			multiToolBox = new multipaneToolBox(
				160, 30, 180, 0, null, null,
				'<g>' +
				'	<rect width="{$width}" height="2000" fill="ghostwhite" opacity="0.8"/>' +
				'</g>'
			);
			multiToolBox.setLabel(90,12," ");
			multiToolBox.getLabel().setAttribute( "text-anchor", "middle");
			multiToolBox.getLabel().setAttribute( "fill", "white");
			multiToolBox.realize(controls);
			multiToolBox.updateLabel(true);

			var paneNames = new Array("Info", "Foo")
			for(i in paneNames )
				multiToolBox.addPaneText(
					svgDocument.getElementById( "text"+paneNames[i] ).firstChild.data,
					paneNames[i]
				);

			multiToolBox.addPaneText(
				svgDocument.getElementById( "textBar" ).firstChild.data,
				"Bar" 
			);

			multiToolBox.addPane( null, "Blank" );

			var paneIndex = multiToolBox.getPaneIndexes();

			panePopupList = new popupList(
				0, 0, paneIndex, updatePane );
			panePopupList.realize( multiToolBox.getMenuBar() );
			setTimeout( "panePopupList.setSelection(0)", 0);

            azap.appendNode(controls);
        }
		function updatePane(item) {
			var itemList = panePopupList.getItemList();
			multiToolBox.changePane( itemList[item] );
		}
